// src/components/Details/details.jsx

import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { useParams } from 'react-router-dom'; // Importamos `useParams`
import '.details.css'

import { getVideogameDetail } from '../../redux/Actions/actions';

function GameDetails({ game, getVideogameDetail }) {
  const { id } = useParams(); // Utilizamos `useParams` para obtener el `id`

  useEffect(() => {
    console.log('Game ID:', id);

    if (id !== null && id !== undefined) {
      getVideogameDetail(id);
    }
  }, [id, getVideogameDetail]);

  console.log('Game Details:', game);

  if (!game) {
    return <p className='details-image'>Cargando detalles del juego...</p>;
  }

  return (
    <div className="game-details">
      <h2>{game.name}</h2>
      <img src={game.background_image} alt={game.name} className="game-details-img" />
      <p>Rating: {game.rating}</p>
      <p>Genres: {game.genres.map((e, index) => (
        <span key={e.id}>
          {game.genres.length > (index + 1) ? `${e.name}, ` : `${e.name}.`}
        </span>
      ))}
      </p>
      {/* Agrega más detalles según sea necesario */}
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    game: state.detail,
  };
};

export default connect(mapStateToProps, { getVideogameDetail })(GameDetails);
