import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import { useParams } from 'react-router-dom';
import './details.css';
import image5 from '../../IMG/f9b67166545aee0783359c566fab740c.gif';
import image6 from '../../IMG/c080dcea703b81541d3249204dcca513578068ee_00.gif';
import image6rep from '../../IMG/c080dcea703b81541d3249204dcca513578068ee_00.gif';
import photo from '../../IMG/fondo_control.jpg'; // Agregué esta línea con la importación de una foto por defecto
import { getVideogameDetail } from '../../redux/Actions/actions';

function Details({ games, getVideogameDetail }) {
  const { id } = useParams();
  const [loading, setLoading] = useState(true);
  const [gameDetails, setGameDetails] = useState(null); // Agregué este estado para almacenar los detalles del juego

  useEffect(() => {
    setLoading(true);

    // Llama a la acción para obtener los detalles del juego
    getVideogameDetail(id)
      .then((response) => {
        console.log("Respuesta de getVideogameDetail:", response);
        setGameDetails(response); // Actualiza el estado con los detalles del juego
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error al obtener los detalles del juego:", error);
        setLoading(false);
      });
  }, [id, getVideogameDetail]);

  const game = games.find((game) => game.id === parseInt(id)) || null;
  console.log("ID del juego:", id);

  if (loading) {
    return (
      <div className="loading-container">
        <img src={image6} alt='Kirby' className="kirby-image loading-gif2" />
        <img src={image5} alt='Cargando...' className="loading-gif1" />
        <img src={image6rep} alt='Kirby' className="kirby-image loading-gif3" />
      </div>
    );
  }

  if (!gameDetails) {
    return <div>No se encontró el juego.</div>;
  }

  return (
    <div className="details-div">
      <div>
        <h3 className="title">{gameDetails.name}</h3>
        {gameDetails.background_image ? (
          <div className="div-img">
            <img src={gameDetails.background_image} alt="Videogame"></img>
          </div>
        ) : (
          <div className="div-img">
            <img src={photo} alt="Videogame"></img>
          </div>
        )}
        <p>
          <strong>Release Date</strong>:{" "}
          {`${gameDetails.released || "None"}`} {/* Cambié releaseDate por released */}
        </p>
        <p>
          <strong>Rating</strong>: ★ {`${gameDetails.rating}`}
        </p>
        {gameDetails.description &&
          gameDetails.genres &&
          gameDetails.platforms ? (
            <div className="div-descr">
              <p className="descripcion">
                {gameDetails.description.replace(/(<([^>]+)>)/gi, "")}
              </p>
              <p>
                <strong>Genres</strong>:{" "}
                {`${gameDetails.genres.join(", ")}`}
              </p>
              <p>
                <strong>Platforms</strong>:{" "}
                {`${
                  typeof gameDetails.platforms === "string"
                    ? gameDetails.platforms
                    : gameDetails.platforms.join(", ")
                  }`}
              </p>
            </div>
          ) : null}
      </div>
    </div>
  );
}

const mapStateToProps = (state) => {
  console.log("Estado de Redux en mapStateToProps:", state);
  return {
    games: state.allGames.games || [],
  };
};

export default connect(mapStateToProps, { getVideogameDetail })(Details);
