      <Router>
        <div className="App">
          <Routes>
            {pathname !== "/home" && <Nav fnct={onSearch} />}
            <Route path="/" element={<LandingPage />} />
            <Route path="/home" element={<HomePage />} />
            {/* Otras rutas y componentes seg√∫n sea necesario */}
          </Routes>
        </div>
      </Router>
    </Provider>
  );
}

export default App;
