function Details({ games, getVideogameDetail }) {
  const { id } = useParams();
  const [loading, setLoading] = useState(true);
  const [game, setGame] = useState(null); // Utilizamos un estado para el juego

  useEffect(() => {
    setLoading(true);

    // Llama a la acción para obtener los detalles del juego
    getVideogameDetail(id)
      .then(() => {
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error al obtener los detalles del juego:", error);
        setLoading(false);
      });
  }, [id, getVideogameDetail]);

  useEffect(() => {
    // Actualiza el estado del juego cuando cambian los datos
    setGame(games.find((game) => game.id.toString() === id) || null);
  }, [games, id]);

  if (loading) {
    // Muestra la pantalla de carga mientras se obtienen los detalles del juego
    return (
      <div className="loading-container">
      return (
     
       <img src={image6} alt='Kirby' className="kirby-image loading-gif2" />
        <img src={image5} alt='Cargando...' className="loading-gif1" />
         <img src={image6rep} alt='Kirby' className="kirby-image loading-gif3" />
      
      </div>
    );
  }

  if (!game) {
    // Muestra un mensaje si no se encuentra el juego
    return <div>No se encontró el juego.</div>;
  }

  // Renderiza los detalles del juego
  return (
    <div className="game-details">
      <h2>{game.name}</h2>
      <img src={game.background_image} alt={game.name} className="game-details-img" />
      <p>Rating: {game.rating}</p>
      <p>Géneros: {game.genres.map((genre, index) => (
        <span key={genre.id}>
          {game.genres.length > (index + 1) ? `${genre.name}, ` : `${genre.name}.`}
        </span>
      ))}
      </p>
      <p>
        <strong>Fecha de lanzamiento</strong>: {game.releaseDate || 'None'}
      </p>
      {game.description && (
        <p>
          <strong>Descripción</strong>: 
          <span dangerouslySetInnerHTML={{ __html: game.description }} />
        </p>
      )}
      {game.platforms && (
        <p>
          <strong>Plataformas</strong>: {typeof game.platforms === 'string' ? game.platforms : game.platforms.join(', ')}
        </p>
      )}
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    games: state.allGames.games || [],
  };
};

export default connect(mapStateToProps, { getVideogameDetail })(Details);
