import React, { useState, useEffect } from 'react';
import Nav from '../Nav/nav';
import SearchBar from '../SearchBar/searchbar';
import Filter from '../Filter/filter';
import { connect } from 'react-redux';

import Pagination from '../Pagination/pagination';
import { getAllGames, getGenres } from '../../redux/Actions/actions';

function Videogames({ allGames, getAllGames, getGenres }) {
  const [currentPage, setCurrentPage] = useState(1);
  const [cardPerPage] = useState(15);

  // Indices de la paginación:
  const indexOfLastCard = currentPage * cardPerPage;
  const indexOfFirstCard = indexOfLastCard - cardPerPage;

  // Cards que se deben mostrar en la pantalla
  const currentCards =
    typeof allGames === 'string'
      ? allGames
      : allGames.slice(indexOfFirstCard, indexOfLastCard);

  const paginate = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  useEffect(() => {
    getAllGames();
    getGenres();
  }, [getAllGames, getGenres]);

  return (
    <div className="container">
      <Nav />
      <SearchBar />
      <Filter/>
      <Pagination
        cardPerPage={cardPerPage}
        totalCards={allGames.length}
        paginate={paginate}
        currentPage={currentPage}
      />
      <div className="games-div">
        {currentCards &&
          (currentCards.length > 1 ? (
            currentCards.map((g) => (
              <Videogame
                key={g.id}
                name={g.name}
                rating={g.rating}
                genres={g.genres}
                image={g.background_image}
                id={g.id}
              />
            ))
          ) : typeof currentCards === 'string' && <p>{currentCards}</p>)}
      </div>
      <Pagination
        cardPerPage={cardPerPage}
        totalCards={allGames.length}
        paginate={paginate}
        currentPage={currentPage}
      />
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    allGames: state.filtered,
  };
};

export default connect(mapStateToProps, { getAllGames, getGenres })(Videogames);
