import React from 'react';

export default function Pagination({ cardPerPage, totalCards, paginate, currentPage }) {

  const pageNumbers = [];
  
  for (let i = 1; i <= Math.ceil(totalCards / cardPerPage); i++) {
    pageNumbers.push(i);
  }

  // Mover la llamada a paginate fuera del bloque de renderizado
  const handlePaginate = () => {
    if (Math.ceil(totalCards / cardPerPage) < currentPage) {
      paginate();
    }
  };

  return (
    <div className="pag-div">
      <ul>
        {pageNumbers.map((p, i) => (
          <li key={i}>
            <button className="pag-btn" onClick={() => paginate(p)}>
              {p}
            </button>
          </li>
        ))}
      </ul>
      {/* Llamar a la función handlePaginate después del renderizado */}
      {handlePaginate()}
    </div>
  );
}
