import React, { useEffect } from 'react';
import { connect, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import './details.css';  
import image5 from '../../IMG/f9b67166545aee0783359c566fab740c.gif';
import image6 from '../../IMG/c080dcea703b81541d3249204dcca513578068ee_00.gif';
import image6rep from '../../IMG/c080dcea703b81541d3249204dcca513578068ee_00.gif';
import { getVideogameDetail } from '../../redux/Actions/actions';

function Details({ getVideogameDetail }) {
  const { id } = useParams();
const { gameDetails, loading } = useSelector((state) => state.detail);

  useEffect(() => {
    // Solo solicita los detalles del juego si no están cargados o en carga
    if (!gameDetails || (!loading && gameDetails.id !== id)) {
      getVideogameDetail(id);
    }
  }, [id, gameDetails, loading, getVideogameDetail]);

  if (loading || !gameDetails) {
    // Si la carga está en progreso o no hay detalles del juego, mostrar un indicador de carga
    return (
      <div>
        <div className="loading-container">
          <img src={image6} alt='Kirby' className="kirby-image loading-gif2" />
          <img src={image5} alt='Cargando...' className="loading-gif1" />
          <img src={image6rep} alt='Kirby' className="kirby-image loading-gif3" />
        </div>
      </div>
    );
  }

  // Resto del código para mostrar los detalles del juego
  return (
    <div className="game-details">
      <h2>{gameDetails.name}</h2>
      <img src={gameDetails.background_image} alt={gameDetails.name} className="game-details-img" />
      <p>Rating: {gameDetails.rating}</p>
      {gameDetails.genres && (
        <p>
          Géneros: {gameDetails.genres.map((e, index) => (
            <span key={e.id}>
              {gameDetails.genres.length > (index + 1) ? `${e.name}, ` : `${e.name}.`}
            </span>
          ))}
        </p>
      )}
      <p>
        <strong>Fecha de lanzamiento</strong>: {gameDetails.releaseDate || 'None'}
      </p>
      {gameDetails.description && (
        <p>
          <strong>Descripción</strong>: {gameDetails.description.replace(/(<([^>]+)>)/gi, '')}
        </p>
      )}
      {gameDetails.platforms && (
        <p>
          <strong>Plataformas</strong>: {typeof gameDetails.platforms === 'string' ? gameDetails.platforms : gameDetails.platforms.join(', ')}
        </p>
      )}
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    game: state.detail,
  };
};

export default connect(mapStateToProps, { getVideogameDetail })(Details);
