// CrearVideogame.js
import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import NavBar from '../Nav/nav';
import axios from 'axios';
import { getAllGames } from '../../redux/Actions/actions';
import './crearvideogame.css';

function CrearVideogame(props) {
  const dispatch = useDispatch();
  const [errors, setErrors] = useState({ form: 'Must complete the form' });

  const [form, setForm] = useState({
    name: '',
    description: '',
    releaseDate: '',
    rating: 0,
    genres: [],
    platforms: [],
  });

  const handleChange = (e) => {
    if (e.target.parentNode.parentNode.id === 'genres') {
      if (e.target.checked) {
        setForm((prevState) => ({
          ...prevState,
          genres: form.genres.concat(e.target.value),
        }));
      } else {
        setForm((prevState) => ({
          ...prevState,
          genres: form.genres.filter((x) => e.target.value !== x),
        }));
      }
    }
    if (e.target.parentNode.parentNode.id === 'platforms') {
      if (e.target.checked) {
        setForm((prevState) => ({
          ...prevState,
          platforms: form.platforms.concat(e.target.name),
        }));
      } else {
        setForm((prevState) => ({
          ...prevState,
          platforms: form.platforms.filter((x) => e.target.name !== x),
        }));
      }
    }
    if (e.target.type !== 'checkbox') {
      setForm((prevState) => ({
        ...prevState,
        [e.target.name]: e.target.value,
      }));
    }
    setErrors(validate({
      ...form,
      [e.target.name]: e.target.value,
    }));
  };

  const validate = (form) => {
    let errors = {};
    if (!form.name) {
      errors.name = 'Game Name is required';
    } else if (form.name.length < 4) {
      errors.name = 'Game Name must have at least 4 characters';
    }
    if (!form.description) {
      errors.description = 'Description is required';
    } else if (form.description.length < 8) {
      errors.description = 'Description must have at least 8 characters';
    }
    if (!form.rating) {
      errors.rating = 'Rating is required';
    } else if (!/^[1-5]$/.test(form.rating)) {
      errors.rating = 'Rating must be between 1 and 5';
    }
    return errors;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    validate(form);
    let checkboxsErrors = [];
    if (form.genres.length < 1) checkboxsErrors.push('Genres is required');
    if (form.platforms.length < 1) checkboxsErrors.push('Platforms is required');
    if (Object.values(errors).length || checkboxsErrors.length) {
      return alert(Object.values(errors).concat(checkboxsErrors).join('\n'));
    }
    axios.post('http://localhost:3001/videogame', form)
      .then((res) => console.log(res.data));
    alert(`${form.name} Creado Correctamente`);

    dispatch(getAllGames());
    props.history.push('/videogames');
  };

  return (
    <>
      <NavBar />
      <div className="main-add">
        <div className="container-add">
          <h2>Â¡CREA TU JUEGO!</h2>
          <div className="div-cont">
            <form onSubmit={handleSubmit} onChange={handleChange}>
              <label htmlFor="name" className="title-name">
                <strong>Name: </strong>
              </label>
              <br />
              <input
                className="name"
                placeholder="Name"
                type="text"
                id="name"
                name="name"
                autoComplete="off"
              />
              <br />
              <label htmlFor="description" className="title-name">
                <strong>Description: </strong>
              </label>
              <br />
              <textarea
                className="name"
                name="description"
                placeholder="Description..."
                id="description"
                cols="30"
                rows="3"
              />
              <br />
              <label htmlFor="date" className="title-name">
                <strong>Release Date: </strong>
              </label>
              <br />
              <input name="releaseDate" className="dt" type="date" id="date" required />
              <br />
              <label htmlFor="rating" className="title-name">
                <strong>Rating: </strong>
              </label>
              <br />
              <input
                name="rating"
                className="dt"
                placeholder="Rate from 1 to 5"
                type="tel"
                id="rating"
                maxLength="1"
                autoComplete="off"
              />
              <br />
              <label className="title-name">
                <strong>Genres:</strong>
              </label>
              <div id="genres" className="genres-div">
                {/* ... rest of your genre checkboxes */}
              </div>
              <label className="title-name">
                <strong>Platforms: </strong>
              </label>
              <div id="platforms" className="plat-div">
                {/* ... rest of your platform checkboxes */}
              </div>
              <br />
              <div className="div-but-form">
                <button type="submit">Create</button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </>
  );
}

export default CrearVideogame;
