import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getVideogameDetail, clean } from '../../redux/actions/actions';
import Navbar from '../Nav/nav';
import photo from '../../img/creacion.jpg';
import './gamedetail.css';

export default function GameDetails(props) {
  const dispatch = useDispatch();
  const gameDetails = useSelector((state) => state.gameDetails);
  const { idVideogame } = props.match.params;

  useEffect(() => {
    dispatch(getVideogameDetail(idVideogame));
    return () => dispatch(clean());
  }, [dispatch, idVideogame]);

  if (!gameDetails) {
    // Puedes retornar null o algún otro componente indicando que está cargando
    return null;
  }

  return (
    <div className="container-detail">
      <Navbar />
      <div className="details-div">
        <div>
          <h3 className="title">{gameDetails.name}</h3>
          {gameDetails.background_image ? (
            <div className="div-img">
              <img src={gameDetails.background_image} alt="Videogame" />
            </div>
          ) : (
            <div className="div-img">
              <img src={photo} alt="Videogame" />
            </div>
          )}
          {gameDetails.releaseDate && (
            <p>
              <strong>Release Date</strong>: {`${gameDetails.releaseDate}`}
            </p>
          )}
          <p>
            <strong>Rating</strong>: ★ {`${gameDetails.rating}`}
          </p>
          {gameDetails.description && gameDetails.genres && gameDetails.platforms && (
            <div className="div-descr">
              <p className="descripcion">
                {gameDetails.description.replace(/(<([^>]+)>)/gi, '')}
              </p>
              <p>
                <strong>Genres</strong>: {`${gameDetails.genres.join(', ')}`}
              </p>
              <p>
                <strong>Platforms</strong>: {`${
                  typeof gameDetails.platforms === 'string'
                    ? gameDetails.platforms
                    : gameDetails.platforms.join(', ')
                }`}
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
