import {React, useState} from 'react'
import { connect } from 'react-redux'
import { searchByName, getAllGames, volverAhome } from '../../redux/actions'



function Searchbar({searchByName, volverAhome}) { 

    const [input, setInput] = useState({ 
        buscar: ''
    }) 

    const handleInputChange = function(e) { 
          setInput({ 
          [e.target.name]: e.target.value
        });
    } 

    const handleOnClick = () => {
        searchByName(input.buscar) 
        setInput({
            buscar: '' 
        });
    } 

    const handleOnClickAll = () => {
     
        volverAhome()
        setInput({
            buscar: ''
        });
    } 

    return (
      <div className="searchbar-div">
        <input
          className="bar-btn"
          name="buscar"
          placeholder="BuscÃ¡ tu juego..." 
          onChange={handleInputChange} 
          value={input.buscar} 
          autoComplete="off"
        ></input>
        <button className="btn" onClick={handleOnClickAll}> 
          Buscar
        </button>
      
      </div>
    );
}

export default connect(null, { searchByName, getAllGames, volverAhome })(Searchbar) // conecta el componente con el store
