import React, { useEffect } from 'react';
import './paginations.css'

export default function Pagination({ cardPerPage, totalCards, paginate, currentPage }) {
  const pageNumbers = [];
  
  for (let i = 1; i <= Math.ceil(totalCards / cardPerPage); i++) {
    pageNumbers.push(i);
  }

  useEffect(() => {
    if (Math.ceil(totalCards / cardPerPage) < currentPage) {
      paginate(1);
    }
  }, [currentPage, totalCards, cardPerPage, paginate]);

  if (pageNumbers.length <= 1) {
    // No se muestran botones si hay una sola página o ninguna
    return null;
  }

  return (
    <div className="pag-div">
      <ul>
        {pageNumbers.map((p, i) => (
          <li key={i}>
            <button className="pag-btn" onClick={() => paginate(p)}>
              {p}
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}
