import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import styles from './landingPage.module.css'; // Importa los estilos como un objeto

import validation from './validations';

export default function Landing() {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });

  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Realizar validaciones utilizando la función de validación
    const validationErrors = validation(formData);
    setErrors(validationErrors);

    // Si no hay errores, puedes realizar la lógica de envío de datos aquí
    if (Object.keys(validationErrors).length === 0) {
      // Aquí puedes realizar la lógica de envío de datos, por ejemplo, enviar el formulario
      console.log('Formulario enviado:', formData);
    }
  };

  return (
    <div className={styles.container}>
      <h1>Iniciar Sesión</h1>
      <form onSubmit={handleSubmit} className={styles.form}>
        <div>
          <label>Email:</label>
          <input
            type="text"
            name="email"
            value={formData.email}
            onChange={handleChange}
          />
          {errors.email && <p className={styles.error}>{errors.email}</p>}
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
          />
          {errors.password && <p className={styles.error}>{errors.password}</p>}
        </div>
        <Link to="/videogames">
          <button type="submit" className={styles.myButton}>
            ENTRAR
          </button>
        </Link>
      </form>
    </div>
  );
}
