// src/Components/Details/details.jsx
import React from 'react';
import { Link } from 'react-router-dom';
import { getVideogameDetail } from '../../redux/Actions/actions';

function GameDetails({ game, match, getVideogameDetail }) {
  useEffect(() => {
    const gameId = match.params.id;
    getVideogameDetail(gameId);
  }, [match.params.id, getVideogameDetail]);

  if (!game) {
    return <p>Cargando detalles del juego...</p>;
  }

  return (
    <div className="game-details">
      <h2>{game.name}</h2>
      <img src={game.background_image} alt={game.name} className="game-details-img" />
      <p>Rating: {game.rating}</p>
      <p>Genres: {game.genres.map((e, index) => (
        <span key={e.id}>
          {game.genres.length > (index + 1) ? `${e.name}, ` : `${e.name}.`}
        </span>
      ))}
      </p>
      {/* Agrega más detalles según sea necesario */}
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    game: state.detail, // Ajusta esto según cómo tengas configurado tu estado
  };
};

export default connect(mapStateToProps, { getVideogameDetail })(GameDetails);
