import React, { useEffect } from 'react';
import { connect, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import './details.css';  
import image5 from '../../IMG/f9b67166545aee0783359c566fab740c.gif';
import image6 from '../../IMG/c080dcea703b81541d3249204dcca513578068ee_00.gif';
import image6rep from '../../IMG/c080dcea703b81541d3249204dcca513578068ee_00.gif';
import { getVideogameDetail } from '../../redux/Actions/actions';

function Details({ getVideogameDetail }) {
  const dispatch = useDispatch() //
  let gameDetails = useSelector((state)=>state.gameDetails) // recibe el estado de gameDetails
  const {idVideogame} = props.match.params; // recibe el id del juego


  useEffect(() => { // se ejecuta cuando se renderiza el componente
    dispatch(getVideogameDetail(idVideogame)); // ejecuta la accion getVideogameDetail
    return ()=> dispatch(clean()) // ejecuta la accion clean
  }, [dispatch, idVideogame]); // se ejecuta cuando cambia el dispatch o el idVideogame

  console.log('Detalles del juego:', games);

  if (!game ) {
    return (
      <div>
        <div className="loading-container">
          <img src={image6} alt='Kirby' className="kirby-image loading-gif2" />
          <img src={image5} alt='Cargando...' className="loading-gif1" />
          <img src={image6rep} alt='Kirby' className="kirby-image loading-gif3" />
        </div>  
      </div>
    );
  }



  return (
    <div className="game-details">
      <h2>{game.name}</h2>
      <img src={game.background_image} alt={game.name} className="game-details-img" />
      <p>Rating: {game.rating}</p>
      <p>Géneros: {game.genres.map((e, index) => (
        <span key={e.id}>
          {game.genres.length > (index + 1) ? `${e.name}, ` : `${e.name}.`}
        </span>
      ))}
      </p>
      <p>
        <strong>Fecha de lanzamiento</strong>: {game.releaseDate || 'None'}
      </p>
      {game.description && (
        <p>
          <strong>Descripción</strong>: {game.description.replace(/(<([^>]+)>)/gi, '')}
        </p>
      )}
      {game.platforms && (
        <p>
          <strong>Plataformas</strong>: {typeof game.platforms === 'string' ? game.platforms : game.platforms.join(', ')}
        </p>
      )}
      

    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    game: state.detail,
  };
};

export default connect(mapStateToProps, { getVideogameDetail })(Details);
