import React, { useEffect } from 'react';
import { connect, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import { getVideogameDetail } from '../../redux/Actions/actions';

import './details.css';

function Details({ getVideogameDetail }) {
  const { id } = useParams();
  const { games } = useSelector((state) => state.allGames);
  const filteredGames = games && games.filter((game) => game.id === id);
  const game = filteredGames && filteredGames.length > 0 ? filteredGames[0] : null;

  useEffect(() => {
    if (game === undefined) {
      console.error("Error al cargar el juego.");
      // Puedes agregar lógica adicional aquí, como mostrar un mensaje de error.
    }
  }, [id, getVideogameDetail]);

  if (!game) {
    return (
      <div className="loading-container">
        <p>Cargando...</p>
      </div>
    );
  }

  return (
    <div className="game-details">
      <h2>{game.name}</h2>
      <img src={game.background_image} alt={game.name} className="game-details-img" />
      <p>Rating: <StarRating rating={game.rating} /></p>
      <p>Géneros: {game.genres.map((e, index) => (
        <span key={e.id}>
          {game.genres.length > (index + 1) ? `${e.name}, ` : `${e.name}.`}
        </span>
      ))}
      </p>
      <p>
        <strong>Fecha de lanzamiento</strong>: {game.releaseDate || 'None'}
      </p>
      {game.description && (
        <p>
          <strong>Descripción</strong>: {game.description.replace(/(<([^>]+)>)/gi, '')}
        </p>
      )}
      {game.platforms && (
        <p>
          <strong>Plataformas</strong>: {typeof game.platforms === 'string' ? game.platforms : game.platforms.join(', ')}
        </p>
      )}
      {/* Agregar aquí la navegación a juegos anteriores y siguientes si es necesario */}
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    game: state.detail,
  };
};

export default connect(mapStateToProps, { getVideogameDetail })(Details);
