// actions.js
import axios from "axios";

const BASE_URL = "https://api.rawg.io";

export function getInitialGames() {
  return function (dispatch) {
    return axios
      .get(`${BASE_URL}/api/games`)
      .then((res) => {
        dispatch({ type: 'GET_INITIAL_GAMES', payload: res.data });
      })
      .catch((err) => {
        return err;
      });
  };
}

export function getAllGames() {
  return function (dispatch) {
    return axios
      .get(`${BASE_URL}/api/games`)
      .then((res) => {
        dispatch({ type: 'GET_ALL_GAMES', payload: res.data });
      })
      .catch((err) => {
        return err;
      });
  };
}

