// src/components/Details/details.jsx

import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { useParams } from 'react-router-dom';
import './details.css';  // Importamos el archivo CSS
import image5 from '../../IMG/';
import image6 from '../../IMG/c080dcea703b81541d3249204dcca513578068ee_00.gif';
import image7 from '../../IMG/god-ofwar-4-4k-m7.jpg'
import { getVideogameDetail } from '../../redux/Actions/actions';

function GameDetails({ game, getVideogameDetail }) {
  const { id } = useParams();

  useEffect(() => {
    console.log('Game ID:', id);

    if (id !== null && id !== undefined) {
      getVideogameDetail(id);
    }
  }, [id, getVideogameDetail]);

  console.log('Game Details:', game);

  if (!game) {
    return (
      <div className='details-image-container'>
        
        
        <img src={image7} alt='fondo' className="imagen-fondo" />
        <img src={image5} alt='Loading...' className="loading-gif" />
        <img src={image5} alt='Loading...' className="loading-gif" />
      </div>
    );
  }

  return (
    <div className="game-details">
      <h2>{game.name}</h2>
      <img src={game.background_image} alt={game.name} className="game-details-img" />
      <p>Rating: {game.rating}</p>
      <p>Genres: {game.genres.map((e, index) => (
        <span key={e.id}>
          {game.genres.length > (index + 1) ? `${e.name}, ` : `${e.name}.`}
        </span>
      ))}
      </p>
      {/* Agrega más detalles según sea necesario */}
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    game: state.detail,
  };
};

export default connect(mapStateToProps, { getVideogameDetail })(GameDetails);
