import axios from "axios";

const BASE_URL = "https://api.rawg.io";

export function searchByName(name) {
  return function (dispatch) {
    return axios
      .get(`${BASE_URL}/api/games?search=${name}`)
      .then((res) => {
        dispatch({ type: 'SEARCH_BY_NAME', payload: res.data });
      })
      .catch((err) => {
        return err;
      });
  };
}

export function getGenres() {
  return async function (dispatch) {
    try {
      var res = await axios.get(`${BASE_URL}/api/genres`);
      return dispatch({
        type: 'GET_GENRES',
        payload: res.data,
      });
    } catch (error) {
      console.log(error);
    }
  };
}

export function getVideogameDetail(id) {
  return function (dispatch) {
    axios
      .get(`${BASE_URL}/api/games/${id}`)
      .then((res) => {
        dispatch({ type: 'GET_VIDEOGAME_DETAIL', payload: res.data });
      })
      .catch((err) => {
        return err;
      });
  };
}

export function getAllGames() {
  return function (dispatch) {
    return axios
      .get(`${BASE_URL}/api/games`)
      .then((res) => {
        dispatch({ type: 'GET_ALL_GAMES', payload: res.data });
      })
      .catch((err) => {
        return err;
      });
  };
}

export function volverAhome() {
  return function (dispatch) {
    dispatch({ type: 'VOLVER_A_HOME' });
  }
}

export function clean() {
  return function (dispatch) {
    dispatch({ type: 'CLEAN' });
  }
}

export function orderBy(order) {
  return function (dispatch) {
    dispatch({ type: 'ORDER_BY', payload: order });
  };
}

export function filterBy(order) {
  return function (dispatch) {
    dispatch({ type: 'FILTER_BY', payload: order });
  };
}

