// src/components/Details/details.jsx

import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { useParams } from 'react-router-dom';
import './details.css';  // Importamos el archivo CSS
// import image5 from '../../IMG/f9b67166545aee0783359c566fab740c.gif';
// import image6 from '../../IMG/c080dcea703b81541d3249204dcca513578068ee_00.gif';
import image6rep from '../../IMG/c080dcea703b81541d3249204dcca513578068ee_00.gif';
// import image7 from '../../IMG/ida-uxvideojuegos-blog.png'
import { getVideogameDetail } from '../../redux/Actions/actions';

function GameDetails({ game, getVideogameDetail }) {
  const { id } = useParams();

  useEffect(() => {
    console.log('Game ID:', id);

    if (id !== null && id !== undefined) {
      getVideogameDetail(id);
    }
  }, [id, getVideogameDetail]);

  console.log('Game Details:', game);

  if (!game) {
    return (
      <div className='details-image-container'>
        <div className="kirby-images-container">
          <img src={image6} alt='Kirby' className="kirby-image loading-gif2" />
          <img src={image6rep} alt='Kirby' className="kirby-image loading-gif3" />
        </div>
        <img src={image5} alt='Loading...' className="loading-gif1" />
      </div>
    );
  }

  return (
    <div className='details-image-container'>
      <div className="kirby-images-container">
        {/* <img src={image6} alt='Kirby' className="kirby-image loading-gif2 image6" /> */}
        <img src={image5} alt='Loading...' className="loading-gif1" />
        {/* <img src={image6rep} alt='Kirby' className="kirby-image loading-gif3 image6rep" /> */}
      </div>

    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    game: state.detail,
  };
};

export default connect(mapStateToProps, { getVideogameDetail })(GameDetails);
